import * as React from 'react'
import './App.css';

interface FavMovieState {
    title?: string
    onButtonClick?: React.MouseEventHandler<HTMLButtonElement>
}


export class App extends React.Component<{}, FavMovieState> {

    constructor(props: {}) {
        super(props);
        this.state = {
            // title: 'HELLO'
        };
    }

    handleClick = () => {
        if (this.state.onButtonClick) {
            alert('The Godfather')
        }
    };

    render() {
        const {title, onButtonClick} = this.state;

        return (
            <>
                <Header/>
                <div>{title}</div>
                <FavMovie onButtonClick={this.handleClick}/>
            </>
        );
    }
}



const Header: React.FunctionComponent = () => (
    <div className={'siteTop'}>
        <h1>LMDB: Lica's Movie Database</h1>
    </div>
);

interface FavMovieProps {
    onButtonClick: React.MouseEventHandler<HTMLButtonElement>
}


const FavMovie: React.FunctionComponent<FavMovieProps> = ({onButtonClick}) => (
    <div>
        <button onClick={onButtonClick}>Fav movie</button>
    </div>
);

export default App;
