import * as React from 'react'
import './App.css';

interface Props {
    onClickButton: React.MouseEventHandler<HTMLButtonElement>
    movie: 'The Godfather'
}

class App extends React.Component<Props> {
    constructor(props: Props) {
        super(props);
        this.state = {
            data: 'Initial data...'
        }
        this.updateState = this.updateState.bind(this);
    };
    updateState(e) {
        this.setState({data: e.target.value});
    }


    render() {
        return (
            <React.Fragment>
                <Header/>
                <movieButton onClickButton={onClickButton, movie}/>
            </React.Fragment>
        );
    }
}

class Content extends React.Component {
    render() {
        return (
            <div>
                <input type = "text" value = {this.props.myDataProp}
                       onChange = {this.props.updateStateProp} />
                <h3>{this.props.myDataProp}</h3>
            </div>
        );
    }
}

const Header: React.SFC = () => (
    <div className={'siteTop'}>
        <h1>LMDB: Lica's Movie Database</h1>
    </div>
);

interface clickButton {
    onClickButton: React.MouseEventHandler<HTMLButtonElement>
    movie: 'The Godfather'
}

const movieButton: React.SFC<clickButton> = ({onClickButton, movie}) => (
    <div className={'body'}>
        <button className={'favMovieButton'} onClick={onClickButton}>
            {movie}
        </button>
    </div>
);



export default App;
